function App() {
    function sendToApi(event) {
        event.preventDefault();
        let files = event.target.files;
        console.log(files);
    }

    return (
        <>
            <div>Choose a picture:</div>
            <form onSubmit={sendToApi}>
                <input
                    type="file"
                    name="uploadFile"
                    accept="image/*"
                    required
                />
                <input type="submit" />
            </form>
        </>
    );
}

export default App;
